#include <iostream>
#include <vector>
#include <map>
#include <set>
#include <string>
#include <string_view>
#include <algorithm>
#include <unordered_map>

using namespace std;

class Solution {
    inline bool is_anagram(string a, string& b) const {
        sort(a.begin(), a.end());
        //	sort(b.begin(), b.end());
        return a == b;
    }

    bool isAnagram(string_view s, string_view t) {
        //	cout << s << " vs " << t << endl;

        map<char, int> m_s;
        map<char, int> m_t;

        auto s_i = s.begin();
        auto t_i = t.begin();

        while (s_i != s.end() && t_i != t.end())
        {
            ++m_s[*s_i++];
            ++m_t[*t_i++];
        }

        return s_i == s.end() && t_i == t.end() && m_s == m_t;
    }
public:
    vector<int> findAnagrams1(string s, string p) {
        vector<int> res;
        bool b = false;
        char c;

        for (int i = 0; i <= s.size() - p.size(); ++i)
        {
            if (p.size() > s.size() - i) break;
            if (b)
            {
                if (s[i + p.size() - 1] == c)
                {
                    res.push_back(i);
                    //b = false;
                }
                b = false;
                continue;
            }

            string sv = s.substr(i, p.size());
            if (isAnagram(sv, p))
            {
                res.push_back(i);
                b = true;
                c = s[i];
            }
        }
        return res;
    }

    vector<int> findAnagrams2(string s, string p) {
        vector<int> res;
        unordered_map<string, vector<int>> u;

        for (int i = 0; i <= s.size() - p.size(); ++i)
        {
            if (p.size() > s.size() - i) break;

            string sv = s.substr(i, p.size());
            sort(sv.begin(), sv.end());
            u[sv].push_back(i);
        }

        sort(p.begin(), p.end());

        return u[p];
    }

    vector<int> findAnagrams(string s, string p) {
        vector<int> res;

        map<char, int> s_map;
        map<char, int> p_map;

        auto s_size = s.size();
        auto p_size = p.size();
        if (p_size > s_size) return res;

        auto p_i = p.begin();
        auto s_i = s.begin();

        while (p_i != p.end())
        {
            ++p_map[*p_i++];
            ++s_map[*s_i++];
        }
        if (s_map == p_map) res.push_back(0);

        for (int i = 1; i <= s_size - p_size; ++i)
        {
            // remove previous element
            auto itf = s_map.find(s[i - 1]);
            --itf->second;
            if (itf->second == 0)
            {
                s_map.erase(itf);
            }

            // add new element
            ++s_map[s[i + p_size - 1]];
            if (p_map == s_map)
            {
                res.push_back(i);
            }
        }
        return res;
    }

};

