#include <iostream>
#include <map>

using namespace std;

class Solution {
public:
    int longestPalindrome(string s) {

        bool b = false;
        bool bb = false;
        map<char, int> m;
        int c = 0;

        for (char c : s)
        {
            ++m[c];
        }


        for (const pair<char, int>& p : m)
        {
            if (p.second % 2 == 0)
            {
                c += p.second;
            }
            else
            {
                if (p.second == 1)
                {
                    b = true;
                }
                else
                {
                    c += p.second - 1;
                }
                bb = true;
            }
        }
        return b || (!b && bb) ? c + 1 : c;

    }
};

int main()
{
    Solution s;
    // "abb"
    // "abcbe"
    // "apmkaamp"
    //"""
    string s1 = ""; //983
    string s2 = ""; //445
    cout << s2.size() << endl;
    cout << s.longestPalindrome("apmkaamp") << endl;
    return 0;
}